INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (9, N'https://render.fineartamerica.com/images/images-profile-flow/400/images/artworkimages/mediumlarge/1/queen-amidala-portrait-da-leonardo-digenio.jpg', N'Padme Amidala', 32, 81, N'fue una líder valiente y esperanzada que fue reina y luego senadora del planeta Naboo')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (10, N'https://i.pinimg.com/originals/80/01/8a/80018ad1c4f472599a9ba987b4afad35.jpg', N'Conde Dooku', 80, 86, N'fue un Lord Sith que lideró la Confederación de Sistemas Independientes como Jefe de Estado durante las Guerras Clon')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (13, N'https://www.sideshow.com/storage/product-images/400313/darth-maul_star-wars_square.jpg', N'Darth Maul', 22, 80, N'fue un zabrak dathomiriano y Lord Sith que vivió durante los últimos años de la República Galáctica.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (14, N'https://www.sideshow.com/storage/product-images/903741/jango-fett_star-wars_square.jpg', N'Jango Fett', 32, 79, N'fue un cazarrecompensas humano mandaloriano, asesino, mercenario, y el "padre" de Boba Fett, un clon suyo, al que tomó como hijo.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (17, N'https://pbs.twimg.com/profile_images/1218947807186423809/m-PWcEHf_400x400.jpg', N'Emperador Palpatine', 65, 75, N'Nació en el planeta Naboo de los Territorios del Borde Medio como Sheev Palpatine, aproximadamente ochenta y cuatro años antes de la Batalla de Yavin. La noble familia Palpatine pertenecía a la clase acomodada de la sociedad de Naboo, aunque en la época de la Era Imperial, no tenía una familia que el público conociera.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (18, N'data:image/jpeg;base64,', N'General Grievous ', 56, 159, N'era un frío y calculador general, estratega brillante y genio militar. También era conocido por la galaxia como uno de los más grandes y mejores cazadores de Jedi y un coleccionista de los sables láser de los Jedi a los que asesinaba, que usaba en combate contra sus enemigos.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (19, N'https://www.sideshow.com/storage/product-images/2172/r2-d2-deluxe_star-wars_square.jpg', N'R2D2', 0, 32, N'un robot del tipo R2, perteneció a las fuerzas de defensa en Naboo sirvió al Rey Veruna durante su término. Este droide siempre estuvo alojado en la Nave Real de Naboo, al igual que otros congéneres con la simple función de reparar cualquier parte de la nave y asistir como navegantes.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (20, N'data:image/jpeg;base64,', N'C3PO', 0, 75, N'fue diseñado para interactuar con seres vivos pensantes, androides y máquinas. Es capaz de comunicarse con más de cien millones de formas de vida. Fue construido por Anakin Skywalker en el planeta Tatooine, para ayudar en las tareas del hogar a su madre.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (21, N'data:image/jpeg;base64,', N'Luke Skywalker', 28, 68, N'fue un legendario héroe de guerra y Jedi que fundó la Nueva Orden Jedi. Era el hijo del Caballero Jedi Anakin Skywalker y la senadora Padmé Amidala de Naboo, además hermano mellizo de Leia Organa.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (22, N'https://d26oc3sg82pgk3.cloudfront.net/files/media/edit/image/2803/square_thumb%403x.jpg', N'Leia Organa', 19, 51, N'Leia fue la hermana melliza de Luke Skywalker, y poco después de su nacimiento se convirtió en la hija adoptiva de Bail Prestor Organa y la reina Breha Antilles Organa de Alderaan, convirtiéndola en princesa de Alderaan.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (23, N'https://pyxis.nymag.com/v1/imgs/6ca/700/5ed153db47b0b6ccb067d29e2429408b00-07-han-solo-harrison-ford.rsquare.w330.jpg', N'Han Solo', 32, 80, N'es un contrabandista al que contrata el antiguo jedi Obi-Wan Kenobi para que le lleve —junto a Luke Skywalker— hasta Alderaan. Dado que debe una gran suma de dinero a Jabba el Hutt, Solo acepta a los pasajeros en el Halcón milenario sin saber que acabará uniéndose a la Alianza Rebelde contra el Imperio galáctico que dominan Darth Vader y el Emperador.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (26, N'https://www.sideshow.com/storage/product-images/902759/chewbacca_star-wars_square.jpg', N'ChewBacca', 200, 110, N' fue un guerrero wookiee, un contrabandista y un luchador de resistencia que peleó en las Guerras Clon, la Guerra Civil Galáctica y el Guerra Fría. Fue criado en el planeta Kashyyyk y se convirtió en un líder militar.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (27, N'https://www.sideshow.com/storage/product-images/300795/darth-vader_star-wars_square.jpg', N'Darth Vader', 41, 136, N'aparece por primera vez en la película original de Star Wars de 1977 como un cíborg despiadado que sirve al Imperio Galáctico. Se le encarga, junto con el gobernador Imperial Wilhuff Tarkin, recuperar los planos secretos de la Estrella de la Muerte, que fueron robados por la Alianza Rebelde.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (28, N'https://www.sideshow.com/storage/product-images/908512/jabba-the-hutt-deluxe_star-wars_square.jpg', N'Jabba the Hutt', 600, 1360, N'se trata de un ser de la especie de los Hutt, nacido en Nal Hutta. A simple vista es un enorme gusano de más de tres metros de largo con una pegajosa lengua y modales bastante pobres. Era un señor del crimen y dirigía un imperio criminal de gran envergadura a lo largo de la galaxia.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (29, N'https://www.sideshow.com/storage/product-images/903351/boba-fett_star-wars_square.jpg', N'Boba Fett', 38, 78, N'fue un cazarrecompensas humano, y el clon del infame cazarrecompensas Jango Fett. Boba fue creado por los clonadores de Kamino y era físicamente idéntico a los soldados clon creados para el Gran Ejército de la República, aunque Boba fue inalterado y no creció aceleradamente como los demás clones.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (30, N'https://www.sideshow.com/storage/product-images/907059/lando-calrissian_star-wars_square.jpg', N'Lando Calrissian', 32, 79, N'es un viejo amigo de Han Solo y el administrador de la Ciudad Nube flotante en el planeta gaseoso Bespin. Antes de los eventos de la película, Lando hizo una carrera como jugador, estafador, playboy, ingeniero de minas y hombre de negocios, y fue el propietario del Halcón Milenario hasta que perdió el barco ante Han en una apuesta')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (31, N'https://www.sideshow.com/storage/product-images/905520/rey-and-d-o_star-wars_square.jpg', N'Rey', 20, 54, N'Rey se presenta como una chatarrera postadolescente que fue abandonada en el planeta Jakku cuando era una niña. Más tarde se involucra en el conflicto de la Resistencia con la Primera Orden. Poderosa con la Fuerza, Rey se entrena para ser un Jedi bajo Luke Skywalker y la General Leia, y se enfrenta a adversarios como Kylo Ren.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (32, N'https://www.sideshow.com/storage/product-images/903096/kylo-ren_star-wars_square.jpg', N'Kylo Ren', 30, 89, N'Es hijo de los personajes Han Solo y Leia Organa, y nieto de Anakin Skywalker y Padmé Amidala, y aprendiz del Líder supremo Snoke pero realmente era aprendiz del Emperador Palpatine.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (33, N'https://www.sideshow.com/storage/product-images/400307/bb-8_star-wars_square.jpg', N'BB8', 0, 2, N'BB-8 es un droide que pertenece al piloto de la Resistencia, Poe Dameron (Oscar Isaac). De hecho, puede encajar en su nave T-70 X-wing. Es un robot esférico con una cabeza abovedada de movimiento libre. Es de color blanco, con detalles en naranja y plata y una lente óptica negra en su casco.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (35, N'https://e0.pxfuel.com/wallpapers/788/805/desktop-wallpaper-star-wars-the-last-jedi-oscar-isaac-poe-dameron-2017-movie-thumbnail.jpg', N'Poe Dameron', 32, 80, N'Poe es un piloto de nave de combate de tipo caza, modelo X-Wing de La Resistencia de la General Leia Organa, y el robot BB-8 es su droide astromecánico. Él es enviado por Leia en una misión al planeta Jakku para recuperar parte de un mapa que conducirá a la ubicación de su hermano, Luke Skywalker.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (36, N'https://e0.pxfuel.com/wallpapers/132/989/desktop-wallpaper-star-wars-the-last-jedi-john-boyega-finn-2017-movie-thumbnail.jpg', N'Finn', 27, 87, N'Finn es un Rebelde de la especie humana y sensible a la fuerza, miembro de la Resistencia y desertor de la Primera Orden. Durante sus primeros años es capturado por la Primera Orden y entrenado como Tropa de asalto')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (37, N'https://hips.hearstapps.com/digitalspyuk.cdnds.net/17/05/1485863852-star-wars-force-awakens-snoke-leader.jpg', N'Snoke', 50, 40, N'es el responsable de la conversión de Ben Solo al lado oscuro de la Fuerza y fue creado y manejado por el Emperador Palpatine')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (38, N'https://www.sideshow.com/storage/product-images/300562/captain-phasma_star-wars_square.jpg', N'Capitan Phasma', 33, 76, N'era una mujer humana soldado de asalto Capitán de la Primera Orden. Sirvió como parte del triunvirato no oficial para la Primera Orden y en operación de la Base Starkiller, haciéndose cargo del mando de los soldados de asalto de la Primera Orden.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (39, N'https://bbts1.azureedge.net/images/p/full/2016/09/b18658c4-9cfe-4e79-8206-a27582d125d8.jpg', N'Maz Kanata', 1500, 3.5, N'fue una mujer pirata que ha vivido por más de mil años. Alrededor de 30 años tras la Batalla de Endor, era propietaria de un castillo que era sede de viajeros, contrabandistas y otras gentes.')
INSERT [dbo].[Personajes] ([IDPersonaje], [Imagen], [Nombre], [Edad], [Peso], [Historia]) VALUES (42, N'https://e1.pxfuel.com/desktop-wallpaper/823/590/desktop-wallpaper-movie-star-wars-episode-vii-the-force-awakens-general-hux-thumbnail.jpg', N'General Hux', 32, 75, N'Se presenta como un comandante despiadado, envuelto en una lucha de poder con Kylo Ren por el liderazgo de la Primera Orden, y solo es superado por el Líder Supremo Snoke.')
